mapper.select:
select
num, title, writer, imgn, hit, reip, bdate
from (
select rownum r_num, a.*
from (
select num, title, writer, imgn, hit,
reip, bdate from upboard
order by 1 desc
) a
) where r_num between
#{begin} and #{end}

mapper.update:
update upboard set title
=#{title},content=#{content},imgn=#{imgn},bdate=sysdate where num =
#{num}

mapper.select:
select num, uwriter, ucontent, reip, uregdate
from (
select rownum r_num, a.*
from (
select num, uwriter, ucontent, reip, uregdate from upboard_comm
where ucode = #{ucode}
order by 1 desc
) a
) where r_num between
#{begin} and #{end}
